sequence.script:

var urlQuery = '';
var expand = mc.getProperty('uri.var.expand');
var parentVersion = mc.getProperty('uri.var.parentVersion');
var start = mc.getProperty('uri.var.start');
var limit = mc.getProperty('uri.var.limit');
var locations = mc.getProperty('uri.var.locations');
var depth = mc.getProperty('uri.var.depth');
if (expand != null && expand != "") {
   urlQuery += 'expand=' + expand + '&';
}
if (parentVersion != null && parentVersion != "") {
   urlQuery += 'parentVersion=' + parentVersion + '&';
}
if (start != null && start != "") {
   urlQuery += 'start=' + start + '&';
}
if (limit != null && limit != "") {
   urlQuery += 'limit=' + limit + '&';
}
if (locations != null && locations != "") {
   if(locations.charCodeAt(0) == '91'){
      locations=eval('(' + locations + ')');
      for(var key in locations){
         urlQuery += 'location=' + locations[key] + '&';
      }
   }else{
      urlQuery += 'location=' + locations + '&';
   }
}
if (depth != null && depth != "") {
   urlQuery += 'depth=' + depth + '&';
}
// Add a leading '?' and remove the trailing '&'
if (urlQuery != "") {
   urlQuery = '?' + urlQuery.toString().substring(0, urlQuery.toString().lastIndexOf("&"));
   mc.setProperty('uri.var.urlQuery', urlQuery);
}
